mapper.insert:
INSERT INTO comment (
    note_id, author_id, parent_id, content,
    like_count, reply_count, created_at, updated_at
) VALUES (
    #{noteId}, #{authorId}, #{parentId}, #{content},
    #{likeCount}, #{replyCount}, #{createdAt}, #{updatedAt}
)

mapper.update:
UPDATE comment
content = #{content}, like_count = #{likeCount}, reply_count = #{replyCount}, updated_at = CURRENT_TIMESTAMP
WHERE comment_id = #{commentId}

mapper.select:
SELECT * FROM comment

    AND note_id = #{params.noteId}
    AND parent_id = #{params.parentId}
    AND author_id = #{params.authorId}

ORDER BY created_at DESC
LIMIT #{pageSize} OFFSET #{offset}

SELECT COUNT(*) FROM comment

    AND note_id = #{params.noteId}
    AND parent_id = #{params.parentId}
    AND author_id = #{params.authorId}
